def test_added_route_ctx_kwargs(app):
    @app.route("/", ctx_foo="foo", ctx_bar=99)
    async def handler(request: Request):
        return empty()

    request, _ = app.test_client.get("/")

    assert request.route.ctx.foo == "foo"
    assert request.route.ctx.bar == 99
