    def __getitem__(self, item):
        # Sort keys in order of specificity
        for key in sorted(self, key=lambda k: ("*" not in k, k), reverse=True):
            if fnmatch(item, key):
                return super().__getitem__(key)
        raise super().__getitem__(item)

