    def _split(value, param_defn, _in):
        default_style = OpenAPIURIParser.style_defaults[_in]
        style = param_defn.get("style", default_style)
        delimiter = QUERY_STRING_DELIMITERS.get(style, ",")
        return value.split(delimiter)

