@setupmethod
def post(self, rule: str, **options: t.Any) -> t.Callable[[T_route], T_route]:
    """Shortcut for :meth:`route` with ``methods=["POST"]``.

    .. versionadded:: 2.0
    """
    return self._method_route("POST", rule, options)

